import React from 'react'
import "./HeroStyles.css";
function Hero(props){
  return (
    <div className={cName}>
      <h1>{props.title}</h1>
      <h2 style={{backgroundColor: '#4c2121', color: 'white', textAlign: 'left', width:335}}></h2>
      <img alt="HeroImg" src="https://cdn.pixabay.com/photo/2015/06/24/15/45/code-820275_1280.jpg"/>
      <div className="hero-text">
        <h1>{props.title}</h1>
        <p>{props.title}</p>
        <a href={props.url} className={props.btnClass}>
          {props.buttonText}
        </a>
        </div>    
    </div>
    )
}

export default Hero;
